(function ($) {

    // 左侧滚动条
    $('.blog-header-container').niceScroll();

    // 导航切换
    $('body').on('click', '.blog-hamburger', function () {
        $(this).toggleClass("is-active");
    });

    // 评论留言
    $('.reply-btn').on('click', function () {
        var parentLi = $(this).parents('li').first(),
            parentID = $(this).data('id'),
            respond = $('#respond'),
            respondHtml = respond.prop("outerHTML");

        $("#respond").remove();
        parentLi.after(respondHtml);
        $("#comment_parent").val(parentID);
        $('.cancel-comment-reply').show();
    });
})(jQuery);

function cancelReply() {
    var respond = $('#respond'),
        respondHtml = respond.prop("outerHTML");

    respond.remove();
    $('.blog-comment').after(respondHtml);
    $("#comment_parent").val('0');
    $('.cancel-comment-reply').hide();
}